function countTableRows() {
    // Get all the table elements
    const tables = document.querySelectorAll('table');

    for (let i = 0; i < tables.length - 1; i++) {
        // Check if the current table has a td with 'Locations' as inner text
        const hasLocationTd = Array.from(tables[i].querySelectorAll('td')).some(td => td.innerText.trim() === 'Locations');

        // If the current table has 'Locations', check the next table for the number of tr elements
        if (hasLocationTd) {
            const nextTable = tables[i + 1];
            return nextTable.querySelectorAll('tr').length;
        }
    }

    // Return 0 if no matching table is found
    return 0;
}

// Call the function and log the result
duplicates = countTableRows();

if (duplicates > 3) {
  return true;
}
return false;
